import React, {useState} from 'react';
import Reg from '../login/children/Reg';
import Login from '../login/children/Login';
import './tabs.css';
import TabContent from './tab-content/TabContent';

const items = [
    {title: "Авторизация", content: <Reg/>},
    {title: "Регистрация", content: <Login/>}
]

const Tabs = ({title, content}) => {

    const [toggleState, setToggleState] = useState(1);

    const toggleTab = (index) => {
        setToggleState(index);
        console.log(index)
    }
   // "tabs active-tabs"

    return (
        <div className='tabs__container'>

            <div className="block-tabs">
                <div className={toggleState === 1 ? "tabs active-tabs" : "tabs"} onClick={() => toggleTab(1)}>{title}</div>
                <div className={toggleState === 2 ? "tabs active-tabs" : "tabs"} onClick={() => toggleTab(2)}>{title}</div>
            </div>

            <div className="content-tabs">
                <TabContent className={toggleState === 1 ? "content active-contents" : "content"}> {content} </TabContent>
                <TabContent className={toggleState === 1 ? "content active-contents" : "content"}> {content} </TabContent>
            </div> 

        </div>
    );
};

export default Tabs;
