import React, {useState} from 'react';
import Reg from '../login/children/Reg';
import Login from '../login/children/Login';
import './tabs.css';
import TabContent from './tab-content/TabContent';


const Tabs = () => {

    const [toggleState, setToggleState] = useState(1);

    const toggleTab = (index) => {
        setToggleState(index);
        console.log(index)
    }
   // "tabs active-tabs"

    return (
        <div className='tabs__container'>


            <div className="content-tabs">
                <TabContent className={toggleState === 1 ? "content active-contents" : "content"} title={'Регистрация'} content={<Reg/>}/>
                <TabContent className={toggleState === 1 ? "content active-contents" : "content"} title={'Авторизация'} content={<Login/>}/>
            </div> 

        </div>
    );
};

export default Tabs;
