import React from 'react';
import "../login/login.css";
import Login from './children/Login';
import Footer from '../footer/footer';
import Reg from './children/Reg';
import "../login/children/function.js";


const Loginn = () => {    
  const tabs = document.getElementById('tabs');
    const content = document.getElementById('contents');

    const changeClass = el => {
        for (let i=0; i < tabs.children.length; i++) {
            tabs.children[i].classList.remove('active');
        }
        el.classList.add('active');
    }

    tabs.addEventListener('click', e => {
        const currTabs = e.target.dataset.btn;
        changeClass(e.target);
        
        for(let i = 0; i < content.children.length; i++) {
            content.children[i].classList.remove('active');
            if (content.children[i].dataset.content === currTabs) {
                content.children[i].classList.add('active');
            }
        } 

    })
  
    return (
        <div className="registration">
          <div className="tabs-block-wrapper">

            <div id="tabs">
              <div className="tab-btn" data-btn="1" onClick={changeClass()}>Регистрация</div>
              <div className="tab-btn active" data-btn="2" onClick={changeClass()}> Авторизация </div>
            </div>

            <div id="contents">
              <div className="content active" data-content="1">
                <Reg/>
              </div>
              <div className="content" data-content="2">
                <Login/>
              </div>
            </div>
          </div>
          <Footer/>
        </div>
    );


};

export default Loginn;
